let filterPositions=[...positions];const positionsContainer=document.querySelector(".positions-container"),displayPositions=()=>{filterPositions.length<1?positionsContainer.innerHTML="<h6>Brak pozycji!!</h6>":positionsContainer.innerHTML=filterPositions.map(i=>{const{id:t,image:n,title:o,price:s}=i;return`<article class="position" data-id="${t}">\n                <img src="${n}"\n                class="position-img img"\n                alt=""\n                />\n                <footer>\n                    <h5 class="position-name">${o}</h5>\n                    <span class="position-price">${s}</span>\n                </footer>\n            </article>`}).join("")};displayPositions();const form=document.querySelector(".input-form"),searchInput=document.querySelector(".search-input");form.addEventListener("keyup",()=>{const i=searchInput.value;filterPositions=positions.filter(t=>t.title.toLowerCase().includes(i)),displayPositions()});const companiesDOM=document.querySelector(".companies"),displayButtons=()=>{const i=["all",...new Set(positions.map(i=>i.company))];companiesDOM.innerHTML=i.map(i=>`<button class="company-btn" data-id="${i}">${i}</button>`).join("")};displayButtons(),companiesDOM.addEventListener("click",i=>{const t=i.target;t.classList.contains("company-btn")&&(filterPositions="all"===t.dataset.id?[...positions]:position.filter(i=>i.company===t.dataset.id),searchInput.value="",displayPositions())});